import { useEffect } from "react";
import { useParams } from "react-router-dom";

const ShowPage = () => {

    const { id } = useParams();

    const getPost = (id) => {
        axios.get(`http://localhost:3000/posts/${id}`).then((res)=>{
        console.log(res)
        })
    };

    useEffect(()=> {
        getPost(id)
    },[])


    return (
        <div>ShowPage</div>
    )
};

export default ShowPage;
